const connectToMongo = require('./db');
const express=require('express')
const cors=require('cors')
connectToMongo();
const app=express();
const port =5000;

app.use(express.json());
app.use(cors());

//available routes
app.use('/api/auth',require('./routes/auth'));

app.listen(port,()=>{
    console.log(`server is running on port ${port}`)
});